    function calcInvoiceTotals() {

        var tax = $('#txtTax').val();
        tax = tax.replace('%', '') * 1;
        tax = tax / 100;


        var subTotal = 0;
        $('#tblinvoice .lineTotal').each(function () {

            subTotal += ($(this).text() * 1);

        });

        $("#subtotal").text(subTotal.toFixed(4));
        $("#tax").text((subTotal * tax).toFixed(4));
        $("#total").text((subTotal + (subTotal * tax)).toFixed(4));

    }
